#include "ImgHtml.h"

#include <iostream>
#include <iomanip>


std::string  ImgHtmlJG::generate(ImgHtml::Payloads& payloads_)
{
    static const char* const  hdr = "\
<html lang=en><meta charset=utf-8><head>\
<style>\
body { font-family: sans-serif; margin: 0; background: #333; }\
.img-top-left { \
  position: relative;\
  bottom: 15px;\
  top: 5px;\
  font-size: 10px;\
}\
h2 {\
  padding: .vw;\
  padding-right: 5px;\
  background: #333;\
  text-align: right;\
  color: white;\
  margin: 0;\
}\
p {\
  background: #333;\
  padding-right: 10px;\
  text-align: right;\
  color: white;\
  margin: 0;\
  font-size: 90%;\
}\
p1 {\
  background: #333;\
  padding-left: 5px;\
  text-align: left;\
  color: white;\
  margin: 0;\
  font-size: 80%;\
}\
a {\
  color: white;\
}\
a:hover {\
  color: #333;\
  background-color: white;\
}\
ul.topnav {\
  font-size: 80%;\
  list-style-type: none;\
  margin: 0;\
  padding: 0;\
  overflow: hidden;\
  position: fixed;\
  top: 0;\
  width: 100%;\
  background-color: #333;\
  z-index: 1000; \
}\
ul.topnav li { float: left; }\
ul.topnav li a\
{\
  background-color: #333;\
  display: block;\
  text-align: center;\
  padding: 3px 4px;\
  text-decoration: none;\
}\
\
ul.topnav li a.active { background-color: grey; }\
\
ul {\
  font-size: 80%;\
  list-style-type: none;\
  margin: 0;\
  padding: 0;\
  overflow: hidden;\
  background-color: #333;\
  }\
\
li {\
  float: left;\
}\
\
li a, .dropbtn {\
  display: inline-block;\
  color: white;\
  text-align: center;\
  padding: 3px 4px;\
  text-decoration: none;\
}\
\
.dropdown:hover .dropbtn {\
  background-color: #333;\
}\
\
li.dropdown {\
  display: inline-block;\
}\
\
.dropdown-content {\
  display: none;\
  left: 20px; \
  position: absolute;\
  min-width: 160px;\
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);\
  z-index: 1;\
}\
\
.dropdown-content a {\
  padding: 2px 8px;\
  background-color: #333;\
  text-decoration: none;\
  display: block;\
  text-align: left;\
}\
\
.dropdown-content a:hover {background-color: #f1f1f1}\
\
.dropdown:hover .dropdown-content {\
  display: block;\
}\n\
</style>\n";

    static const char*  js = "<script>\n$(function() {\n// Select all links with hashes\n$('a[href*=\"\\\\#\"]')\n  // Remove links that don't actually link to anything\n  .not('[href=\"\\\\#\"]')\n  .not('[href=\"\\\\#0\"]')\n  .click(function(event) {\n    // On-page links\n    if (\n      location.pathname.replace(/^\\//, '') == this.pathname.replace(/^\\//, '')\n      &&\n      location.hostname == this.hostname\n    ) {\n      // Figure out element to scroll to\n      var target = $(this.hash);\n      target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');\n      // Does a scroll target exist?\n      if (target.length) {\n        // Only prevent default if animation is actually gonna happen\n        event.preventDefault();\n        $('html, body').animate({\n          scrollTop: target.offset().top\n        }, 1000, function() {\n          // Callback after animation\n          // Must change focus!\n          var $target = $(target);\n          $target.focus();\n          if ($target.is(\":focus\")) { // Checking if the target was focused\n            return false;\n          } else {\n            $target.attr('tabindex','-1'); // Adding tabindex for elements not focusable\n            $target.focus(); // Set focus again\n          };\n        });\n      }\n    }\n  });\n});\n</script>\n<script>\n$(document).ready(function() {\n  var i = 0;\n  var len = $('div[id^=\"photo_block\"]').length;\n\n  function  _scrollNext() {\n    i = ++i % len;\n    var target = \"#photo_block\" + i;\n    $('html,body').animate({scrollTop: $(target).offset().top}, 1000)\n  };\n  function _scrollPrev() {\n    if (!i) i = len;\n    --i;\n    var target = \"#photo_block\" + i;\n    $('html,body').animate({scrollTop: $(target).offset().top}, 1000)\n  };\n\n\n  $('#next').click(function() {\n    _scrollNext();\n    return false;\n  });\n\n  $('#prev').click(function() {\n    _scrollPrev();\n    return false;\n  });\n\n  var rating_hide = 0;\n\n  function _toggleRatings(r_)\n  {\n    $('.flex-item').each(function(){\n      var  item_rating = $(this).attr(\"rating\");\n      if (rating_hide == 1) {\n\t$(this).addClass(\"entry-visible\");\n      }\n      else {\n\t  if (item_rating < r_) {\n\t    $(this).removeClass(\"entry-visible\")\n\t  }\n\t  else {\n\t    $(this).addClass(\"entry-visible\");\n\t  }\n      }\n    });\n    \n    if (rating_hide == 1) {\n      $('.ToggleRatings').text(\"Only Show Rated (showing all)\");\n    }\n    else {\n      $('.ToggleRatings').text(\"Show All (showing rated)\");\n    }\n    rating_hide = !rating_hide;\n  }\n  function _showRatingGT(r_)\n  {\n    $('.flex-item').each(function(){\n      var  item_rating = $(this).attr(\"rating\");\n      if (item_rating < r_) {\n\t$(this).removeClass(\"entry-visible\");\n      }\n      else {\n\t$(this).addClass(\"entry-visible\");\n      }\n    });\n    $('.ToggleRatings').text(\"Show All \" + \"(showing >=\" + r_ + \")\");\n    rating_hide = 0;\n  }\n\n  $('body').keydown(function(evnt) \n  {\n    switch (evnt.keyCode) {\n      case 74:\n      case 39:\n\tevnt.preventDefault();\n\t_scrollNext();\n\tbreak;\n\n      case 75:\n      case 37:\n\tevnt.preventDefault();\n\t_scrollPrev();\n\tbreak;\n\n      case 48:  //0\n      case 49:\n      case 50:\n      case 51:\n      case 52:\n      case 53:\n\t_showRatingGT(evnt.keyCode - 48);\n\tbreak;\n\n      case 82:  //r\n       _toggleRatings(1);\n       break;\n    }\n  });\n\n  $('#toggle_ratings').click(function() {\n    _toggleRatings(1);\n  });\n});\n</script>\n";


    std::ostringstream  html;
    std::ostringstream  nav;
    std::ostringstream  body;
    std::ostringstream  jginit;

    html << hdr
         << "<style>\n" << ImgHtmlJG::_css_justified_gallery << "</style>\n"
         << "<script>\n" << ImgHtmlJG::_js_jquery << "</script>\n"
         << "<script>\n" << ImgHtmlJG::_js_justified_gallery << "</script>\n"
	 << js << "\n"
	 << "</head>\n<body>\n\n"
	 << "<div class=\"ul.topnav\" style=\"z-index: 1000; width: 100%; position:fixed; top:0;\">"
	 << "<ul>"
	 <<   "<li class=\"dropdown\"><a href=\"#top\" class=\"dropbtn\"><b>Image Catalogue</b></a>\n"
	 <<     "<div class=\"dropdown-content\">";

    unsigned  pblck = 0;
    for (auto& p : payloads_)
    {
        ImgIdx&        idx    = p.idx;
        ImgThumbGens&  thumbs = p.thumbs;
        
        std::cout << "  working on [" << std::setw(3) << idx.size() << "]  " << idx.id << "  " << std::flush;

	nav << "  <a href=\"#photo_block" << pblck << "\">" << idx.id << "</a>";
        jginit << "$(\"#photo_block" << pblck << "\").justifiedGallery({\n\
rowHeight: 200,\n\
lastRow: 'justify',\n\
margins: 5,\n\
border: 20\n\
});\n";
	++pblck;

        body << "<h2>/<a href=\"" << idx.id << "\">" << idx.id << "</a>"
	     << "<sup><a href=\"#\" class=\"show_hide\" rel=\"#photo_block" << pblck << "\">[-]</sup></a>"
	     << "</h2>\n";

        if (idx.empty()) {
            std::cout << '\n';
            continue;
        }

        idx.sort();

        body << "  <p>ttl=<b>" << idx.size() << "</b>";
        for (ImgIdx::const_iterator  dtsf=idx.begin(); dtsf!=idx.end(); ++dtsf)
        {
            if (dtsf->key.dt.hms.empty()) {
                continue;
            }
            body << ",  " << dtsf->key.dt.hms;

            for (ImgIdx::const_reverse_iterator  dts=idx.crbegin(); dts!=idx.crend(); ++dts) {
                if (dts->key.dt.hms.empty()) {
                    continue;
                }

                body << " .. " << dts->key.dt.hms;
                break;
            }

            break;
        }
        body << "</p>\n";

        const ImgIdx::Stats  stats = idx.stats();
        {
            struct P {
                const char*  category;
                const ImgIdx::Stats::_Stat&  stat;
            }
            all[] = {
                { "cameras",    stats.camera },
                { "lenses",     stats.lens },
                { "focal lens", stats.focallen },
                { NULL,         stats.camera },
            };
            P*  p = all;
            while (p->category) {
                body << "  <p1>";
                for (const auto&  si : p->stat) {
                    body << "<b>" << (si.first.empty() ? "???" : si.first.c_str()) << "</b> #" << si.second << " ";
                }
                body << "  </p1><br>\n";
                ++p;
            }
        }
        body << " <div id=\"photo_block" << pblck-1 << "\" style=\"background-color: white;\">\n";
        
        for (auto& t : thumbs)
        {
            const ImgData&  img = t->img();

            // insert the caption for the cell
            body << "  <div class=flex-item  rating=" << (img.rating.empty() ? "0" : img.rating.c_str()) << "  title=\"";
            if (!img.rating.empty())
            {
                const std::string&  s = img.rating;

                long  l = -1;

                if ((!isdigit(s[0])) && (s[0] != '-') && (s[0] != '+')) {
                }
                else
                {
                   char*  p;
                   l = strtol(s.c_str(), &p, 10);
                   if (*p == NULL) {
                       if (l > 5) {
                           // shouldnt happen
                           l = -1;
                       }
                   }
                }

                if (l < 0) {
                    body << '[' << s << ']';
                }
                else
                {
                    body << '[';
                    long  l1 = 5-l;
                    while (l1-- > 0) {
                        body << '-';
                    }
                    while (l-- > 0) {
                        body << '*';
                    }
                    body << ']';
                }
                body << '\n';
            }


            body << img.title << "  " << t->idx().key.dt.hms << "\n"
		 << img.xy << "\n"
                 << img << "\">\n"
                 << "  <a href=\"" << img.filename << "\"><img src=\"" << t->prevpath() << "\"></a>";

            if (t->idx().imgs.size() > 1)
            {
                body << "    <div class=img-top-left>";
                ImgIdx::Imgs::const_iterator  alts = t->idx().imgs.begin();
                while (alts != t->idx().imgs.end()) {
                    const ImgData&  altimg = *alts++;
                    body << "<a href=\"" << altimg.filename << "\"># </a>";
                }
                body << "\n"
		     << "    </div>\n";
            }
            body << "  </div>\n";
                
            std::cout << '.' << std::flush;
        }
        body << " </div>\n";
        std::cout << std::endl;
    }

    html << nav.str()
	 <<     "</div>\n"
	 <<   "</li>\n"
         <<   "<li><a href=\"#\" id=\"prev\">&#8593 Prev</a></li>"
         <<   "<li><a href=\"#\" id=\"next\">Next &#8595</a></li>"
         <<   "<li><a href=\"#\" id=\"next\">Next &#8595</a></li>"
         <<   "<li><a class=\"ToggleRatings\" href=\"#\" id=\"toggle_ratings\">Show Only Rated</a></li>"
	 << "</ul>"
	 << "</div>"
	 << "<div id=\"top\" style=\"padding: 20px 8px;\">"
         << body.str()
         << "</div>  <!-- padding -->\n"
         << "<script>\n"
         << jginit.str() << '\n'
         << "</script>\n"
         << "</body></html>";
    return std::move(html.str());
}



